p; &nbsp; &nbsp; @foreach($etiquetas as $etiqueta)<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"{{ route(\'gallery.index\', [\'etiqueta_id\' =&gt; $etiqueta-&gt;id]) }}\"<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;class=\"uppercase&nbsp; text-gray-400 hover:text-blue-600\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; {{ $etiqueta-&gt;name }}<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/a&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/li&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; @endforeach<br>&nbsp; &nbsp; &lt;/ul&gt;<br>&lt;/div&gt;<br>&nbsp;&lt;!-- Formulario de bÃºsqueda --&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;form method=\"GET\" action=\"{{ route(\'gallery.index\') }}\" class=\"mb-8\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;div class=\"flex items-center border-b border-orange-500 py-2\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;input type=\"text\" name=\"title\" placeholder=\"Search by title\" value=\"{{ request(\'title\') }}\"<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; class=\"appearance-none border-none w-full text-teal-500 mr-3 py-1 px-2 leading-tight focus:outline-none\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;button type=\"submit\"<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; class=\"flex-shrink-0 bg-teal-500 hover:bg-teal-700 border-teal-500 hover:border-teal-700 text-sm border-4 text-white py-1 px-2 rounded\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Buscar<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/button&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/form&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/h1&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;div class=\"masonry\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @foreach ($posts as $post)<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;div class=\"masonry-item bg-white shadow-md rounded-lg overflow-hidden\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;img src=\"{{ asset(\'storage/\' . $post-&gt;img) }}\" alt=\"{{ $post-&gt;title }}\"<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; class=\"w-full h-100 object-cover\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;div class=\"p-4\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"{{ route(\'gallery.detalle\', [\'title\' =&gt; $post-&gt;title]) }}\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;h2 class=\"text-xl font-bold\"&gt;{{ $post-&gt;title }}&lt;/h2&gt;<br><br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;div class=\"flex space-x-4 mt-4\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @if ($post-&gt;url)<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"{{ $post-&gt;url }}\" target=\"_blank\" class=\"text-blue-500\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;i class=\"fas fa-link\"&gt;&lt;/i&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/a&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @endif<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @if ($post-&gt;ins)<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"{{ $post-&gt;ins }}\" target=\"_blank\" class=\"text-pink-500\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;i class=\"fab fa-instagram\"&gt;&lt;/i&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/a&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @endif<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @if ($post-&gt;face)<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"{{ $post-&gt;face }}\" target=\"_blank\" class=\"text-blue-700\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;i class=\"fab fa-facebook\"&gt;&lt;/i&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/a&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @endif<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @if ($post-&gt;youtube)<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a data-fancybox=\"gallery\"href=\"{{ $post-&gt;youtube }}\" target=\"_blank\"<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; class=\"text-red-500\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;i class=\"fab fa-youtube\"&gt;&lt;/i&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/a&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @endif<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;<br><br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;p class=\"mt-2\"&gt;{{ $post-&gt;excerpt }}&lt;/p&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;div class=\"mt-4\"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @foreach ($post-&gt;etiquetas as $etiqueta)<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a href=\"{{ route(\'gallery.index\', [\'etiqueta_id\' =&gt; $etiqueta-&gt;id]) }}\"<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; class=\"inline-block bg-gray-200 text-gray-700 text-xs font-semibold mr-2 px-2.5 py-0.5 rounded\"&gt;{{ $etiqueta-&gt;name }}&lt;/a&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @endforeach<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @endforeach<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &lt;/div&gt;<br><br>&nbsp; &nbsp; @stop</pre><p><br></p><p><strong>route:&nbsp;</strong></p><pre>Route::get(\'/gallery\', [GalleryController::class, \'index\'])-&gt;name(\'gallery.index\');</pre>', 'gallery.png', 'http://laravel12.test/gallery', NULL, NULL, NULL, '2025-03-21 11:19:14', '2025-03-21 11:59:53'),
(641, 'pagina /posts al projecte laravel12.test (blog cassic)', 23, 'pagina /posts al projecte laravel12.test (blog cassic)', '<p>route:&nbsp;</p><pre>Route::get(\'/posts\', [PostController::class, \'index\'])-&gt;name(\'posts.index\');</pre><p>funcio a controlller: C:\\projectes\\LARAVEL\\laravel12\\app\\Http\\<strong>Controllers</strong>\\PostController.php</p><pre>public function index(Request $request)\n    {\n        $query = Post::with([\'category\', \'etiquetas\']);\n\n        if ($request-&gt;has(\'search\')) {\n            $query-&gt;where(\'title\', \'like\', \'%\' . $request-&gt;search . \'%\');\n        }\n\n        // Ordenar los resultados por el campo \'updated_at\' en orden descendente\n        $query-&gt;orderBy(\'updated_at\', \'desc\');\n        $posts = $query-&gt;get();\n\n        // Obtener todas las categorÃ­as para la lista horizontal\n        $categories = Category::all();\n\n        return view(\'posts.index\', compact(\'posts\', \'categories\'));\n    }</pre><p>view:C:\\projectes\\LARAVEL\\laravel12\\resources\\<strong>views</strong>\\posts\\index.blade.php</p><pre>@extends(\'layouts.prova\')\n@section(\'ruta\', \'/\')\n@section(\'title\', \'posts\')\n@section(\'descripcio\', \'entrades blog una columna vertical de tailblocks i amb un filtre per categories\')\n@section(\'content\')\n\n    &lt;section class=\"text-gray-600 body-font overflow-hidden\"&gt;\n        &lt;div class=\"container px-5 mx-auto\"&gt;\n\n            &lt;!-- ponerle tailwind background image a este div --&gt;\n            &lt;div class=\"container w-full mx-auto text-center bg-cover bg-center mb-8\"&gt;\n                &lt;h1\n                    class=\"mb-2 text-4xl font-extrabold leading-none tracking-normal text-gray-900 md:text-6xl md:tracking-tight text-center\"&gt;@yield (\'ruta\')&lt;span class=\"block w-full py-2 text-transparent bg-clip-text leading-12 bg-gradient-to-r from-green-400 to-purple-500 lg:inline\"&gt;@yield          (\'title\')&lt;/span&gt;\n                &lt;/h1&gt;\n                &lt;p class=\"font-mono font-light italic text-center \"&gt;&lt;a href=\"https://tailblocks.cc/\"&gt;@yield (\'descripcio\')\n                    &lt;/a&gt;&lt;/p&gt;\n            &lt;/div&gt;\n            &lt;!-- Lista horizontal de categorÃ­as --&gt;\n            &lt;div class=\"flex flex-wrap justify-center py-4 mb-8\"&gt;\n                @foreach ($categories as $category)\n                    &lt;a href=\"{{ route(\'posts.category\', $category-&gt;id) }}\"\n                        class=\"uppercase text-gray-900 font-extrabold hover:text-teal-700 mx-2\"&gt;\n                        {{ $category-&gt;name }}\n                    &lt;/a&gt;\n                @endforeach\n            &lt;/div&gt;\n            &lt;!-- Formulario de bÃºsqueda --&gt;\n            &lt;form method=\"GET\" action=\"{{ route(\'posts.index\') }}\" class=\"mb-8\"&gt;\n                &lt;div class=\"flex items-center border-b border-orange-500 py-2\"&gt;\n                    &lt;input type=\"text\" name=\"search\" placeholder=\"Fes una cerca per el nom de l\'entrada\"\n                        class=\"appearance-none border-none w-full text-teal-500 mr-3 py-1 px-2 leading-tight focus:outline-none\"&gt;\n                    &lt;button type=\"submit\"\n                        class=\"flex-shrink-0 bg-teal-500 hover:bg-teal-700 border-teal-500 hover:border-teal-700 text-sm border-4 text-white py-1 px-2 rounded\"&gt;\n                        Buscar\n                    &lt;/button&gt;\n                &lt;/div&gt;\n            &lt;/form&gt;\n            &lt;div class=\"-my-8 divide-y-2 divide-gray-100\"&gt;\n                @foreach ($posts as $post)\n                    &lt;div class=\"py-8 flex flex-wrap md:flex-nowrap\"&gt;\n                        &lt;div class=\"md:w-64 md:mb-0 mb-6 flex-shrink-0 flex flex-col\"&gt;\n                            &lt;a href=\"{{ route(\'posts.category\', $post-&gt;category-&gt;id) }}\"\n                                class=\"font-semibold title-font text-gray-700\"&gt;{{ $post-&gt;category-&gt;name }}&lt;/a&gt;\n                            &lt;span class=\"mt-1 text-gray-500 text-sm\"&gt;{{ $post-&gt;updated_at }}&lt;/span&gt;\n                            &lt;img data-fancybox src=\"/storage/{{ $post-&gt;img }}\" align=\"left\" width=\"250\"\n                                height=\"\" /&gt;\n                        &lt;/div&gt;\n                        &lt;div class=\"md:flex-grow\"&gt;\n                            &lt;div class=\"max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-lg\"&gt;\n                                &lt;a href=\"/posts/detalle/{{ $post-&gt;id }}\"&gt;\n                                    &lt;h1 class=\"text-2xl font-bold mb-4 break-words\"&gt;{{ $post-&gt;title }}&lt;/h1&gt;\n                                    &lt;div class=\"flex space-x-4 mt-4\"&gt;\n                                        @if ($post-&gt;url)\n                                            &lt;a href=\"{{ $post-&gt;url }}\" target=\"_blank\" class=\"text-blue-500\"&gt;\n                                                &lt;i class=\"fas fa-link\"&gt;&lt;/i&gt;\n                                            &lt;/a&gt;\n                                        @endif\n                                        @if ($post-&gt;ins)\n                                            &lt;a href=\"{{ $post-&gt;ins }}\" target=\"_blank\" class=\"text-pink-500\"&gt;\n                                                &lt;i class=\"fab fa-instagram\"&gt;&lt;/i&gt;\n                                            &lt;/a&gt;\n                                        @endif\n                                        @if ($post-&gt;face)\n                                            &lt;a href=\"{{ $post-&gt;face }}\" target=\"_blank\" class=\"text-blue-700\"&gt;\n                                                &lt;i class=\"fab fa-facebook\"&gt;&lt;/i&gt;\n                                            &lt;/a&gt;\n                                        @endif\n                                        @if ($post-&gt;youtube)\n                                            &lt;a data-fancybox=\"gallery\" href=\"{{ $post-&gt;youtube }}\" target=\"_blank\"\n                                                class=\"text-red-500\"&gt;\n                                                &lt;i class=\"fab fa-youtube\"&gt;&lt;/i&gt;\n                                            &lt;/a&gt;\n                                        @endif\n                                    &lt;/div&gt;\n                                    &lt;div class=\"mb-4\"&gt;\n                                        @foreach ($post-&gt;etiquetas as $etiqueta)\n                                            &lt;a href=\"{{ route(\'posts.etiqueta\', $etiqueta-&gt;id) }}\"\n                                                class=\"text-blue-500 hover:underline\"&gt;{{ $etiqueta-&gt;name }}&lt;/a&gt;\n                                        @endforeach\n                                    &lt;/div&gt;\n                                    &lt;p class=\"mb-4\"&gt;{!! $post-&gt;excerpt !!}&lt;/p&gt;\n                                    @php\n                                        $directory = public_path(\'img/people/\' . $post-&gt;title);\n                                    @endphp\n                                    @if (file_exists($directory) &amp;&amp; is_dir($directory))\n                                        &lt;a href=\"{{ route(\'gallery.detalle\', [\'title\' =&gt; $post-&gt;title]) }}\"\n                                            class=\"text-blue-500 hover:underline\"&gt;\n                                            &lt;i class=\"fas fa-folder-open text-yellow-500 mr-2\"&gt;&lt;/i&gt;\n                                            View Gallery\n                                        &lt;/a&gt;\n                                    @else\n                                        &lt;p class=\"text-red-500\"&gt;No images available&lt;/p&gt;\n                                    @endif\n                            &lt;/div&gt;\n                        &lt;/div&gt;\n                    &lt;/div&gt;\n                @endforeach\n            &lt;/div&gt;\n        &lt;/div&gt;\n    &lt;/section&gt;\n\n@stop\n<br></pre>', 'posts.png', 'http://laravel12.test/posts', NULL, NULL, NULL, '2025-03-21 13:27:58', '2025-03-21 13:27:58'),
(642, 'pagina /books al projecte laravel12.test ', 23, 'pagina /books al projecte laravel12.test ', '<p>pagina /books al projecte laravel12.test. Es tracta d\'una gal.leria de llibres pdf.</p><p>route:C:\\projectes\\LARAVEL\\laravel12\\routes\\web.php</p><pre>Route::get(\'/books\', [BookController::class, \'index\'])-&gt;name(\'books.index\');</pre><p>controller:C:\\projectes\\LARAVEL\\laravel12\\app\\Http\\Controllers\\BookController.php</p><pre>&lt;?php\n\nnamespace App\\Http\\Controllers;\n\nuse App\\Models\\Book;\nuse Illuminate\\Http\\Request;\n\nclass BookController extends Controller\n{\n    public function index(Request $request)\n    {\n        // Obtenir tots els llibres disponibles\n        $books = Book::paginate(12);\n\n        return view(\'books.index\', compact(\'books\'));\n    }\n}\n<br></pre><p>view: C:\\projectes\\LARAVEL\\laravel12\\resources\\views\\books\\index.blade.php</p><pre>@extends(\'layouts.prova\')\n@section(\'ruta\', \'/books\')\n@section(\'title\', \'Galeria de Llibres\')\n@section(\'descripcio\', \'Explora la nostra colÂ·lecciÃ³ de llibres en format digital\')\n\n@section(\'content\')\n\n    &lt;section class=\"text-gray-600 body-font\"&gt;\n        &lt;div class=\"container px-5 mx-auto\"&gt;\n\n            &lt;!-- Header amb imatge de fons --&gt;\n            &lt;div class=\"w-full mx-auto text-center bg-cover bg-center mb-8\"\n                style=\"background-image: url(\'/path/to/books-banner.jpg\');\"&gt;\n                &lt;h1\n                    class=\"mb-2 text-4xl font-extrabold leading-none tracking-normal text-gray-900 md:text-6xl md:tracking-tight text-center\"&gt;\n                    @yield(\'ruta\')\n                    &lt;span\n                        class=\"block w-full py-2 text-transparent bg-clip-text leading-12 bg-gradient-to-r from-blue-400 to-green-500 lg:inline\"&gt;\n                        @yield(\'title\')\n                    &lt;/span&gt;\n                &lt;/h1&gt;\n                &lt;p class=\"font-mono font-light italic text-center\"&gt;@yield(\'descripcio\')&lt;/p&gt;\n            &lt;/div&gt;\n\n            &lt;!-- Galeria de llibres --&gt;\n            &lt;div class=\"grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6\"&gt;\n                @foreach ($books as $book)\n                    &lt;div class=\"bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300\"&gt;\n\n\n                        &lt;a data-fancybox data-type=\"pdf\" href=\"/storage/{{ $book-&gt;pdf_url }}\"&gt;{{ $book-&gt;title }}\n\n                            &lt;img src=\"/storage/{{ $book-&gt;img }}\" class=\"w-full h-60 object-cover rounded-md mb-4\"\n                                alt=\"{{ $book-&gt;title }}\"&gt;\n                            &lt;h2 class=\"text-lg text-cyan-600 font-medium title-font mb-4 text-center break-words\"&gt;\n                                {{ $book-&gt;title }}&lt;/h2&gt;\n                        &lt;/a&gt;\n                    &lt;/div&gt;\n                @endforeach\n            &lt;/div&gt;\n\n            &lt;!-- PaginaciÃ³ --&gt;\n            &lt;div class=\"mt-6\"&gt;\n                {{ $books-&gt;links() }}\n            &lt;/div&gt;\n\n        &lt;/div&gt;\n    &lt;/section&gt;\n\n@endsection\n<br></pre>', 'books.png', 'http://laravel12.test/books', NULL, NULL, NULL, '2025-03-21 13:35:14', '2025-03-21 13:35:14'),
(643, 'pagina arrencada / projecte larpreline.test', 23, 'pagina arrencada / projecte larpreline.test', '<p>pagina arrencada / projecte larpreline.test</p>', 'welcomelarpreline.png', 'http://larpreline.test/', NULL, NULL, NULL, '2025-03-22 08:20:43', '2025-03-22 08:20:43'),
(644, 'pagina indexgallery del projecte larpreline.test/', 23, 'pagina indexgallery del projecte larpreline.test/', '<p>route: C:\\projectes\\LARAVEL\\larpreline\\routes\\web.php</p><pre>Route::get(\'/indexgallery\', [PostController::class, \'indexGallery\'])-&gt;name(\'posts.indexGallery\');</pre><p>controller:C:\\projectes\\LARAVEL\\larpreline\\app\\Http\\Controllers\\PostController.php</p><pre>public function indexGallery(Request $request)\n{\n    $query = Post::with([\'category\', \'etiquetas\']);\n\n    // Aplicar bÃºsqueda\n    if ($request-&gt;has(\'search\')) {\n        $query-&gt;where(\'title\', \'like\', \'%\' . $request-&gt;search . \'%\');\n    }\n\n    // Aplicar filtro de categorÃ­a\n    if ($request-&gt;has(\'category_id\')) {\n        $query-&gt;where(\'category_id\', $request-&gt;category_id);\n    }\n\n    // Aplicar filtro de etiqueta\n    if ($request-&gt;has(\'etiqueta_id\')) {\n        $query-&gt;whereHas(\'etiquetas\', function ($q) use ($request) {\n            $q-&gt;where(\'etiquetas.id\', $request-&gt;etiqueta_id);\n        });\n    }\n\n    // Ordenar los resultados por el campo \'updated_at\' en orden descendente\n    $query-&gt;orderBy(\'updated_at\', \'desc\');\n    $posts = $query-&gt;get();\n\n    // Obtener todas las categorÃ­as para la lista horizontal\n    $categories = Category::all();\n\n    return view(\'posts.index_gallery\', compact(\'posts\', \'categories\'));\n}</pre><p>view:C:\\projectes\\LARAVEL\\larpreline\\resources\\views\\posts\\index_gallery.blade.php</p><pre>@extends(\'layouts.uikit\')\n@section(\'title\', \'Gal.leria de posts\')\n@section(\'subtitle\', \'amb cards gallery Zzseba78\')\n@section(\'content\')\n\n    &lt;section class=\"uk-section uk-section-small uk-section-default uk-background-secondary\"&gt;\n        &lt;div class=\"uk-container uk-container-expand uk-margin-large-bottom\"&gt;\n            &lt;ul class=\"uk-subnav uk-subnav-pill\"&gt;\n                &lt;li class=\"uk-active\" data-uk-filter-control&gt;&lt;a href=\"{{ route(\'posts.indexGallery\') }}\"&gt;Show All&lt;/a&gt;&lt;/li&gt;\n                @foreach ($categories as $categoria)\n                    &lt;li data-uk-filter-control=\".{{ strtolower(str_replace(\' \', \'-\', $categoria-&gt;name)) }}-card\"&gt;\n                        &lt;a href=\"#\"&gt;{{ $categoria-&gt;name }}&lt;/a&gt;\n                    &lt;/li&gt;\n                @endforeach\n            &lt;/ul&gt;\n            &lt;div class=\"uk-grid uk-grid-medium uk-child-width-1-2@s uk-child-width-1-3@m uk-child-width-1-4@l uk-child-width-1-5@xl uk-grid-match js-filter\"\n                data-uk-grid=\"masonry: true\"&gt;\n                @foreach ($posts as $post)\n                    &lt;div class=\"{{ strtolower(str_replace(\' \', \'-\', $post-&gt;category-&gt;name)) }}-card\"\n                        data-tags=\"{{ strtolower($post-&gt;title . \' \' . $post-&gt;etiquetas-&gt;pluck(\'name\')-&gt;implode(\' \')) }}\"&gt;\n                        &lt;div class=\"uk-card uk-card-small uk-card-default\"&gt;\n                            &lt;div class=\"uk-card-header\"&gt;\n                                &lt;div class=\"uk-grid uk-grid-small uk-text-small\" data-uk-grid&gt;\n                                    &lt;div class=\"uk-width-expand\"&gt;\n                                        &lt;span class=\"cat-txt\"&gt;{{ $post-&gt;category-&gt;name }}&lt;/span&gt;\n                                    &lt;/div&gt;\n                                    &lt;div class=\"uk-width-auto uk-text-right uk-text-muted\"&gt;\n                                        &lt;span data-uk-icon=\"icon:clock; ratio: 0.8\"&gt;&lt;/span&gt; {{ $post-&gt;updated_at }}\n                                    &lt;/div&gt;\n                                &lt;/div&gt;\n                            &lt;/div&gt;\n                            &lt;div class=\"uk-card-media\"&gt;\n                                &lt;div class=\"uk-inline-clip uk-transition-toggle\" tabindex=\"0\"&gt;\n                                    &lt;img class=\"lazy\" data-src=\"storage/{{ $post-&gt;img }}\" data-uk-img\n                                        alt=\"{{ $post-&gt;title }}\"&gt;\n                                &lt;/div&gt;\n                            &lt;/div&gt;\n                            &lt;div class=\"uk-card-body\"&gt;\n                                &lt;a href=\"{{ route(\'posts.detalle\', $post-&gt;id) }}\"&gt;\n                                    &lt;h4 class=\"uk-margin-small-bottom uk-margin-remove-adjacent uk-text-bold\"&gt;\n                                        {{ $post-&gt;title }}\n                                    &lt;/h4&gt;\n                                &lt;/a&gt;\n                                &lt;p class=\"uk-text-small uk-text-muted\"&gt;{{ $post-&gt;excerpt }}&lt;/p&gt;\n                                &lt;div class=\"uk-flex uk-flex-wrap\"&gt;\n                                    @if ($post-&gt;url)\n                                        &lt;a href=\"{{ $post-&gt;url }}\" target=\"_blank\" class=\"uk-icon-link\"\n                                            uk-icon=\"icon: link\"&gt;&lt;/a&gt;\n                                    @endif\n                                    @if ($post-&gt;ins)\n                                        &lt;a href=\"{{ $post-&gt;ins }}\" target=\"_blank\" class=\"uk-icon-link\"\n                                            uk-icon=\"icon: instagram\"&gt;&lt;/a&gt;\n                                    @endif\n                                    @if ($post-&gt;face)\n                                        &lt;a href=\"{{ $post-&gt;face }}\" target=\"_blank\" class=\"uk-icon-link\"\n                                            uk-icon=\"icon: facebook\"&gt;&lt;/a&gt;\n                                    @endif\n                                    @if ($post-&gt;youtube)\n                                        &lt;a data-fancybox=\"gallery\" href=\"{{ $post-&gt;youtube }}\" target=\"_blank\"\n                                            class=\"uk-icon-link\" uk-icon=\"icon: youtube\"&gt;&lt;/a&gt;\n                                    @endif\n                                &lt;/div&gt;\n                                &lt;div class=\"uk-flex uk-flex-wrap\"&gt;\n                                    @foreach ($post-&gt;etiquetas as $etiqueta)\n                                        &lt;a href=\"#\" data-uk-tooltip=\"title: {{ $etiqueta-&gt;name }}\"\n                                            data-uk-filter-control=\"[data-tags*=\'{{ strtolower(str_replace(\' \', \'-\', $etiqueta-&gt;name)) }}\']\"&gt;\n                                            {!! $etiqueta-&gt;svg !!}\n                                        &lt;/a&gt;\n                                    @endforeach\n                                &lt;/div&gt;\n                            &lt;/div&gt;\n                        &lt;/div&gt;\n                    &lt;/div&gt;\n                @endforeach\n            &lt;/div&gt;\n        &lt;/div&gt;\n    &lt;/section&gt;\n\n    &lt;script type=\"text/javascript\"&gt;\n        function filterCards(value) {\n            const searchValue = value.toLowerCase().trim();\n            const cards = document.querySelectorAll(\'[data-tags]\');\n            cards.forEach(card =&gt; {\n                const tags = card.getAttribute(\'data-tags\').toLowerCase();\n                if (tags.includes(searchValue)) {\n                    card.style.display = \'\';\n                } else {\n                    card.style.display = \'none\';\n                }\n            });\n        }\n    &lt;/script&gt;\n\n\n@stop\n<br></pre>', 'indexgallery.png', 'http://larpreline.test/indexgallery', NULL, NULL, NULL, '2025-03-22 09:39:32', '2025-03-22 09:39:32'),
(645, 'pagina /bootstrap_gallery del projecte larpreline.test', 23, 'pagina /bootstrap_gallery del projecte larpreline.test', '<p>pagina /bootstrap_gallery del projecte larpreline.test</p>', 'bootstrapgallery.png', 'http://larpreline.test/bootstrap_gallery', NULL, NULL, NULL, '2025-03-22 17:16:05', '2025-03-22 17:16:05'),
